import React, { useState } from "react";
import API from "../api";
import { useNavigate } from "react-router-dom";

export default function Register({ setUser }) {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const nav = useNavigate();

  const submit = async (e) => {
    e.preventDefault();
    try {
      const { data } = await API.post("/auth/register", { name, email, password });
      localStorage.setItem("token", data.token);
      localStorage.setItem("user", JSON.stringify(data.user));
      setUser(data.user);
      nav("/channels");
    } catch (err) {
      alert(err.response?.data?.message || "Registration failed");
    }
  };

  return (
    <div className="auth-wrap">
      <div className="form-card">
        <h2>Create account</h2>
        <form onSubmit={submit}>
          <div className="form-row"><input required placeholder="Name" value={name} onChange={e=>setName(e.target.value)} /></div>
          <div className="form-row"><input required placeholder="Email" value={email} onChange={e=>setEmail(e.target.value)} /></div>
          <div className="form-row"><input required type="password" placeholder="Password" value={password} onChange={e=>setPassword(e.target.value)} /></div>
          <div className="form-row"><button className="btn" type="submit">Register</button></div>
        </form>
        <p>Have an account? <a href="/login" className="link">Login</a></p>
      </div>
    </div>
  );
}
