import React, { useEffect, useState } from "react";
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import Login from "./pages/Login";
import Register from "./pages/Register";
import Channels from "./pages/Channels";
import Chat from "./pages/Chat";
import { socket } from "./socket";

export default function App() {
  const [user, setUser] = useState(() => {
    const u = localStorage.getItem("user");
    return u ? JSON.parse(u) : null;
  });

  useEffect(() => {
    if (user) {
      socket.auth = { token: localStorage.getItem("token") };
      socket.connect();
      // no global joinApp used in this simple server; channel join is per-chat
    }
    return () => {
      if (user) {
        socket.disconnect();
      }
    };
  }, [user]);

  return (
    <BrowserRouter>
      <Routes>
        <Route path="/login" element={<Login setUser={setUser} />} />
        <Route path="/register" element={<Register setUser={setUser} />} />
        <Route path="/channels" element={user ? <Channels user={user} setUser={setUser} /> : <Navigate to="/login" />} />
        <Route path="/chat/:channelId" element={user ? <Chat user={user} /> : <Navigate to="/login" />} />
        <Route path="/" element={<Navigate to={user ? "/channels" : "/login"} />} />
      </Routes>
    </BrowserRouter>
  );
}
