import React, { useEffect, useState, useRef, useCallback } from "react";
import { useParams } from "react-router-dom";
import API from "../api";
import MessageList from "../components/MessageList";
import { socket } from "../socket";
import ThemeToggle from "../components/ThemeToggle";

export default function Chat({ user }) {
  const { channelId } = useParams();
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState("");
  const [page, setPage] = useState(1);
  const [hasMore, setHasMore] = useState(true);
  const [typingUsers, setTypingUsers] = useState([]);
  const bottomRef = useRef(null);
  const typingTimeout = useRef(null);
  const lastEmit = useRef(0);

  useEffect(() => {
    if (!channelId) return;
    fetchMessages(1);

    if (socket.connected) {
      socket.emit("joinChannel", channelId);
    } else {
      socket.connect();
      socket.emit("joinChannel", channelId);
    }

    const onNew = (msg) => {
      // only add messages for the same channel
      if (!msg) return;
      if (String(msg.channel) !== String(channelId) && msg.channel !== channelId) return;
      // mark as new to trigger animation
      const msgWithFlag = { ...msg, isNew: true };
      setMessages((prev) => [...prev, msgWithFlag]);
      // remove the flag shortly after so animation can replay next time
      setTimeout(() => {
        setMessages((prev) => prev.map((m) => (m._id === msg._id ? { ...m, isNew: false } : m)));
      }, 700);
      scrollToBottom();
    };

    const onTyping = ({ userId }) => {
      if (!userId || userId === (user.id || user._id)) return;
      setTypingUsers((t) => {
        if (t.includes(userId)) return t;
        return [...t, userId];
      });
      // remove after 2s if no update
      setTimeout(() => {
        setTypingUsers((t) => t.filter((id) => id !== userId));
      }, 2000);
    };

    socket.on("newMessage", onNew);
    socket.on("typing", onTyping);

    return () => {
      socket.emit("leaveChannel", channelId);
      socket.off("newMessage", onNew);
      socket.off("typing", onTyping);
    };
    // eslint-disable-next-line
  }, [channelId]);

  const fetchMessages = async (p = 1) => {
    try {
      const { data } = await API.get(`/messages/${channelId}?page=${p}&limit=25`);
      if (data.length < 25) setHasMore(false);
      if (p === 1) setMessages(data);
      else setMessages((prev) => [...data, ...prev]);
      setPage(p);
      scrollToBottom();
    } catch (err) {
      console.error(err);
    }
  };

  const scrollToBottom = () => {
    setTimeout(() => bottomRef.current?.scrollIntoView({ behavior: "smooth" }), 80);
  };

  // Debounced typing emitter
  const emitTyping = useCallback(() => {
    const now = Date.now();
    if (now - lastEmit.current < 400) return; // don't spam
    lastEmit.current = now;
    socket.emit("typing", { channelId, userId: user.id || user._id });
  }, [channelId, user]);

  const onChange = (e) => {
    setText(e.target.value);
    emitTyping();

    // clear previous timeout
    if (typingTimeout.current) clearTimeout(typingTimeout.current);
    typingTimeout.current = setTimeout(() => {
      // optionally we could emit "stopTyping" here
    }, 1500);
  };

  const sendMessage = () => {
    if (!text.trim()) return;
    const payload = {
      text: text.trim(),
      sender: user.id || user._id,
      channel: channelId,
      createdAt: new Date()
    };
    socket.emit("sendMessage", payload);
    setText("");
    // optimistic add is optional (we rely on server emit)
  };

  const loadOlder = () => {
    if (!hasMore) return;
    fetchMessages(page + 1);
  };

  return (
    <div className="app">
      <div className="container">
        <aside className="sidebar" style={{ width: 220 }}>
          <div style={{ fontWeight: 700 }}>Channel</div>
          <div style={{ color: "var(--muted)", marginTop: 6 }}>{channelId}</div>
        </aside>

        <main className="main">
          <div className="header">
            <h2>Chat</h2>
            <div className="controls">
              <div className="sub">Realtime chat</div>
              <ThemeToggle />
            </div>
          </div>

          <div className="chat-wrap">
            <div className="chat-toolbar">
              <button className="load-more" onClick={loadOlder} disabled={!hasMore}>Load older</button>
            </div>

            <div className="messages" role="log" aria-live="polite">
              <MessageList messages={messages} currentUserId={user.id || user._id} />
              <div ref={bottomRef} />
            </div>

            <div className="composer">
              <div style={{ flex: 1 }}>
                <input value={text} onChange={onChange} placeholder="Write a message..." />
                {typingUsers.length > 0 && (
                  <div className="typing-indicator" aria-live="polite">
                    <div className="typing-dots" aria-hidden>
                      <span></span><span></span><span></span>
                    </div>
                    <div style={{ marginLeft: 8 }}>{typingUsers.length > 1 ? "Multiple people typing..." : "Someone is typing..."}</div>
                  </div>
                )}
              </div>

              <button className="send-btn" onClick={sendMessage}>Send</button>
            </div>
          </div>
        </main>
      </div>
    </div>
  );
}
